import React, { useState } from "react";
import img from "../Images/todo.png";
const Todo = () => {
  const [data, setData] = useState("");
  const [items, setItems] = useState([]);
  const addItem=()=>{
    setItems((prevData)=>{
        return []
    })
  }
  return (
    <>
      <div className="main-div">
        <div className="child-div">
          <figure>
            <img src={img} alt="todoLogo" />
            <figcaption>Add your list here ‚úåüèª</figcaption>
          </figure>
          <div className="addItems">
            <input
              type="text"
              placeholder="‚úçüèªAdd an item..."
              name=""
              value={data}
              onChange={(e) => {
                setData(e.target.value);
              }}
            />
            <i className="fa-solid fa-plus add-btn" title="Add Item" onClick={addItem}></i>
          </div>
          <div className="showItems">
            <div className="eachItem">
              <h3>Apple</h3>
              <i className="fa-solid fa-trash" title="Delete Item"></i>
            </div>
          </div>
          <div className="showItems">
            <button className="btn effect04" data-sm-link-text="Remove All">
              <span>CHECK LIST</span>
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default Todo;
